$(function(){$("#login-btn").click(function(){var e=$("#username").val(),r=$("#pwd").val();if(!e||!r)return!1;$.ajax({url:"http://127.0.0.1:8848/login.php",type:"POST",data:"username="+e+"&pwd="+r,success:function(r){"fail"===r?(alert("用户名或者密码错误!"),$("#username").css("border","2px solid red").next().html("用户名出错").addClass("error"),$("#pwd").css("border","2px solid red").next().html("密码出错").addClass("error")):(localStorage.setItem("uid",r),localStorage.setItem("username",e),alert("欢迎回来!"+e),location.href="/")}}),o()});var o=function(){$(".form-input").blur(function(){$(".form-group ").removeClass("error"),$(".form-group span").html(""),$(".form-input").css("border","1px solid #ccc")})}});